        else
            return (
                <>
                    <span><a href="/login">login</a></span>
                    <span><a href="/registro">cadastre-se</a></span>
                </>
            )
    }

    return(
        <>
        <nav>
            <h1>Riide</h1>
            <div className="container-navbar">
              <input placeholder={`para onde deseja ir?`} /><a href="/"><img id="find" src={find}/></a>
                {showUser()}
            </div>
        </nav>
        </>
    )
}
