import React from 'react'
import './navbar.css'

export default function Navbar({history}){
    const name = localStorage.getItem('name')
    function showUser(){
        if(name)
            return (
               <>
                    <span>{name}</span>
               </>
            )
        else
            return (
                <>
                    <span onClick={() => history.push('/login')}>login</span>
                    <span><a href="/">cadastre-se</a></span>
                </>
            )
    }

    return(
        <>
        <nav>
            <h1>TableMeal</h1>
            <div className="container-navbar">
                {showUser()}
            </div>
        </nav>
        </>
    )
}
