import React from 'react'
import {browserHistory} from 'react-router';
import './navbar.css'

export default function Navbar({history}){
    const name = localStorage.getItem('name')
    function showUser(){
        if(name)
            return (
               <>
                    <span>{name}</span>
               </>
            )
        else
            return (
                <>
                    <span onClick={() => browserHistory.push('/login')}>Login</span>
                    <span onClick={() => browserHistory.push('/')}>Cadastre-se</span>
                </>
            )
    }

    return(
        <>
        <nav>
            <h1>TableMeal</h1>
            <div className="container-navbar">
                {showUser()}
            </div>
        </nav>
        </>
    )
}
