import React from 'react'
import './navbar.css'

export default function Navbar(){
    const user = localStorage.getItem('user')
    function showUser(){
        if(user)
            return (
               <>
                    <span>{user}</span>
               </>
            )
        else
            return (
                <>
                    <span><a href="/login">login</a></span>
                    <span><a href="/">cadastre-se</a></span>
                </>
            )
    }

    return(
        <>
        <nav>
            <h1>TableMeal</h1>
            <div className="container-navbar">
                {showUser()}
            </div>
        </nav>
        </>
    )
}
